<?php\n\nnamespace App\\Http\\Requests;\n\nuse Illuminate\Foundation\\Http\\FormRequest;\n\nclass StoreInventoryItemRequest extends FormRequest\n{\n    /**\n     * Determine if the user is authorized to make this request.\n     */\n    public function authorize(): bool\n    {\n        return true;\n    }\n\n    /**\n     * Get the validation rules that apply to the request.\n     *\n     * @return array<string, \\Illuminate\\Contracts\\Validation\\ValidationRule|array<mixed>|string>\n     */\n    public function rules(): array\n    {\n        return [\n            \'serial_number\' => \'required|string|unique:inventory_items,serial_number,\'.($this->inventory_item->id ?? \'\'),\n            \'device_equipment_name\' => \'required|string\',\n            \'department\' => \'required|string\',\n            \'reference_number\' => \'required|string|unique:inventory_items,reference_number,\'.($this->inventory_item->id ?? \'\'),\n            \'monetary_value\' => \'required|numeric\',\n            \'item_category_id\' => \'required|exists:item_categories,id\',\n        ];\n    }\n}\n